import React from "react";
import './intro.css';
import bg from './bg.png';
import { Link } from "react-scroll";

const Intro =() => {
    return(
        <section id="intro" >
            <div className="introcontent">
            <span className="1"> Hello </span> <br />
            <span className="2">I'm </span><span className="3"> Umamaheswari Tamilselvan </span><br />
            <span className="1"> Frontend developer! </span>
            <p> Experienced in this field for morethan 1 year. </p>
            <Link classID="buttonhire">Hire Me</Link>
            </div>
            <img src={bg} alt="ProfilePic" className="bg" />
        </section>
    );
}

export default Intro;
